import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';
import Responsive from '../common/ResponsiveHeader';
const PostListBlock = styled(Responsive)`
  margin-top: 3rem;
  
`;

const MypageTemplateBlock = styled.div`
display: flex;
 width: 50rem;
 height: 30rem;
 display: flex;
margin-bottom:1rem;
border: thin solid ;
border-color: #424242;
box-shadow: 5px 5px 5px rgba(10, 10, 10, 0.3);
`;
const MypageText =({title, maxlength, text})=>{

  return (
    <div Style="height: 3.5rem; width: 20rem; border: solid thin; border-radius: 5px; ">
      <p Style="margin-top:2px; margin-bottom:1px; margin-left:4px; font-size:13px; font-weight:800;">{title}</p>
      <p Style="margin-top:0px;margin-left:8px; height:2rem; width: 19.5rem; font-size:1.2rem; border:none; outline:none;"maxlength={maxlength}>
      {text}
    </p>
    </div>);
}
const MypageInput =({title, maxlength})=>{


  return (
    <div Style="height: 3.5rem; width: 20rem; border: solid thin; border-radius: 5px; ">
      <p Style="margin:0; font-size:13px; font-weight:800;">{title}</p>
      <input type='text' Style="height:2rem; width: 19.5rem; font-size:1.2rem; border:none; outline:none;"maxlength={maxlength}/>
    </div>);
}


const MyInfoBlock =()=>{
  const email='example@google.com';
  return(
    <MypageTemplateBlock>
      <h1>개인 정보</h1>
      <MypageText title="이메일 주소" maxlength='65' text={email}/>
    <></>
  </MypageTemplateBlock>
  )
};

const MypageTemplate = ({ children }) => {
  return (
    <PostListBlock>
    <MyInfoBlock/>
      
    </PostListBlock>
  );
};

export default MypageTemplate;
