import React, { useEffect } from "react";
import qs from "qs";
import { useParams,useLocation } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import PostList from "../../components/post/PostList";
import { listPosts } from "../../modules/posts";

const PostListContainer = () => {
  const dispatch = useDispatch();
  const location = useLocation();

  const { serach_type, serach_contents,posts, error, loading, user } = useSelector(
    ({ posts, loading, user }) => ({
      serach_type: posts.serach_type,
      serach_contents: posts.serach_contents,
      posts: posts.posts,
      error: posts.error,
      loading: loading['posts/LIST_POSTS'],
      user: user.user,
    }),
  );

  const onChangeField = ()=>{
    dispatch()
  }

  const params = useParams();
  useEffect(() => {
   
    const { username } = params;
    const { page } = qs.parse(location.search, {
      ignoreQueryPrefix: true,
    });
    dispatch(listPosts({ username, page }));
  }, [dispatch, location.search]);


  return (
    <PostList
      serach_type={serach_type}
      serach_contents={serach_contents}
      onChangeField={onChangeField}
      loading={loading}
      error={error}
      posts={posts}
      showWriteButton={user}
    />
  );
};

export default PostListContainer;
