import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams, useLocation, useNavigate  } from "react-router-dom";
import post, { readPost, unloadPost } from "../../modules/post";
import PostViewer from "../../components/post/PostViewer";
import PostManagement from "../../components/post/PostManagement";
import { deletePost } from "../../modules/write";
const PostManagementContainer = () => {

  const navigate = useNavigate();
  // 처음 마운트될 때 포스트 읽기 API 요청
  const { id } = useParams();
  const dispatch = useDispatch();

  const { post, error, loading, user } = useSelector(
    ({ post, loading, user }) => ({
      post:post.post,
      error: post.error,
      loading: loading['post/READ_POST_SUCCESS'],
      user: user.user,
    }),
  );


  const onEdit =()=>{

  }
  const onRemove =()=>{
    e.preventDefault();
    dispatch(deletePost(id));
  }
  const onBack=()=>{
    navigate(-1);
  }

  return <PostManagement user={user} post={post}loading={loading} id={id} onEdit={onEdit} onRemove={onRemove} onBack ={onBack} />;
};

export default PostManagementContainer;
