import styled from 'styled-components';
import Responsive from './Responsive';
import Button from './Button';
import { Link } from 'react-router-dom';

const NavBlock = styled.div`
position: fixed;
height: 100%;  
width: 12rem;
background: #81F781;
box-shadow: 0px 4px 2px rgba(0, 0, 0, 0.08);


`;
const Wrapper = styled.ul`
margin:0;

font-weight: 600;
list-style:none;
width:100%;
padding-left:0;

border-bottom: solid thin;
 
`;
const Icon = styled.img`
 width:25px;
 height:25px;
 color:#81F781;
`
const Menu = styled.li`
padding: 15px;
font-size: 1.2rem;
color:#088A68;
display: normal;
width:100%;
border-top: solid thin;

&:hover{
  color:#81F781;
  background:#04B486;
}
&:active{
  color:#81F781;
  background:#088A68;
}
`

const Spacer = styled.nav`


`;

const Nav = () => {
    return (
        <>
        <NavBlock>
            <Wrapper>
              <Icon src={process.env.PUBLIC_URL +"menu-icon.png"}/>
              <Menu><Icon src={process.env.PUBLIC_URL +"post-icon.png"}/>게시판</Menu>
              <Menu><Icon src={process.env.PUBLIC_URL +"menu-icon.png"}/>모임</Menu>
              <Menu><Icon src={process.env.PUBLIC_URL +"menu-icon.png"}/>미션&amp;식물</Menu>
              <Menu><Icon src={process.env.PUBLIC_URL +"menu-icon.png"}/>마이페이지</Menu>
              
           </Wrapper>
        </NavBlock>
        <Spacer/>
        
        </>
    );
};

export default Nav;
