import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from "react-router-dom";
import {WhitePostsItemBox} from '../common/WhiteBox';
const PostListBlock = styled(Responsive)`
  
`;

const WritePostButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;

`;
const PostItemBlock = styled(WhitePostsItemBox)`
`
const PostItemInfoBlock = styled.div`

  padding-top: 1rem;
  padding-bottom: 1rem;

  &:first-child {
    padding-top: 0;
  }
  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h2 {
    font-size: 2rem;
    margin-bottom: 0;
    margin-top: 0;
    &:hover {
      color: ${palette.gray[6]};
    }
  }

  p {
    margin-top: 2rem;
  }
`;

const SubInfo = styled.div`
  /* margin-top: 1rem; */
  color: ${palette.gray[6]};

  /* span 사이에 가운뎃점 문자 보여 주기 */
  span + span::before {
    color: ${palette.gray[4]};
    padding-left: 0.25rem;
    padding-right: 0.25rem;
    content: '\\B7'; /* 가운뎃점 문자 */
  }
`;
const Title= styled.h2`
  margin:0;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow:hidden; 
  text-overflow:ellipsis;
  width:40rem;
`


const Image = styled.img`
width: 10rem;
height: 10rem;
border: solid thin;
margin: 1rem;
`
const PostNickName = styled.div`
width: 10rem; 
height:2rem;
font: bold;
margin:0;
`
const PostContent = styled.div`
margin:0;
width: 33rem; 
height:3rem;
overflow: hidden;
text-overflow: ellipsis;
white-space: nowrap;
`;

const PostItem = ({ post }) => {

    return (
      <Link to={`/post/view/${post.post_id}`}>
      <PostItemBlock>
        
        <Image src={process.env.PUBLIC_URL + "/eco-icon.png"}/>
        <PostItemInfoBlock>
        <Title>{post.post_title}</Title>
        <PostNickName><b>{post.user_name}</b></PostNickName>
        {post.post_contents}
        {post.post_regdate}
       
      </PostItemInfoBlock>
   
    </PostItemBlock>
    </Link>
    );
  };
  const PostList = ({ posts, loading, error, showWriteButton }) => {
    if (error) {
      return <PostListBlock>게시판을 불러올 수 없습니다...</PostListBlock>
    }
  /**에러처리 */
    return (
      <PostListBlock>
        <WritePostButtonWrapper>
        {showWriteButton && (<Button cyan to="/post/edit">
            새 글 작성하기
          </Button>)}
        </WritePostButtonWrapper>
        {!loading && posts && (<div>
          {

          posts.map(post => (
            <PostItem post={post} key={post.post_id} />
          ))}
        </div>)}
      </PostListBlock>
    );
  };
  
  export default PostList;
