import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from "react-router-dom";
import {WhitePostsItemBox} from '../common/WhiteBox';
const ReplyListBlock = styled.div`
  
`;

const ReplyItemBlock = styled.div`
display: flex;

margin-top:0.5rem;
margin-bottom:0.5rem;
border: thin solid ;
border-color: #424242;
box-shadow: 5px 5px 5px rgba(10, 10, 10, 0.1);
padding:1rem 1.2rem 0rem 1.2rem;
flex-wrap: nowrap;
font-size: 0.9rem;
`
const NickName = styled.div`
padding:2rem;
`

const Contents = styled.div`
`
const Regdate = styled.div`
`

const ReplyItemInfoBlock = styled.div`

    display: flex;
  padding-top: 1rem;
  padding-bottom: 1rem;
  flex-direction:row;
  &:first-child {
    padding-top: 0;
  }
  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h2 {
    font-size: 2rem;
    margin-bottom: 0;
    margin-top: 0;
    &:hover {
      color: ${palette.gray[6]};
    }
  }

  p {
    margin-top: 2rem;
  }
`;



const PostContent = styled.div`
font-size: 1rem;
padding-top:0;
`;

const ReplyItem = ({ reply }) => {

    return (
    
    <ReplyItemBlock>
        <ReplyItemInfoBlock>
            <NickName>{reply.reply_name}</NickName>
            <Contents>{reply.reply_contents}</Contents>
  
        </ReplyItemInfoBlock>
    </ReplyItemBlock>
    );
};
  const ReplyList = ({ replys, loading, error, showWriteButton }) => {
    
    const replysArray = [
        {
            reply_id: 0,
            reply_name: '유저1',
            reply_contents: '테스트 내용이에요',
            reply_regdate:'2022.10.10',
        },
        {
            reply_id: 1,
            reply_name: '유저2',
            reply_contents: '테스트 내용이에요',
            reply_regdate:'2022.10.11',
        }
    ]
    if (error) {
      return <>게시판을 불러올 수 없습니다...</>
    }
  /**에러처리 */
    return (
      <ReplyListBlock>
       <div>
          {
            replysArray.map(reply => (
            <ReplyItem reply={reply} key={reply.reply_id} />
          ))}
        </div>
        {!loading && replys && (<div>
          {
            replys.map(reply => (
            <ReplyItem reply={reply} key={reply.reply_id} />
          ))}
        </div>)}
      </ReplyListBlock>
    );
  };
  
  export default ReplyList;
