import styled from 'styled-components';
import Responsive from './Responsive';
import Button from './Button';
import { Link } from 'react-router-dom';


const ContentsBoxBlock = styled.div`
    height: calc( 100% - 4rem ) ;  
    width: calc( 100% - ${(props)=>props.menu_width}rem );
    margin-left: ${(props)=>props.menu_width}rem;
    margin-right: 2rem;
    background: red;

  
`;



const ContentsBox = ({  hidingState }) => {
    const menu_width= hidingState?4:13;
  return (
    <>

      <ContentsBoxBlock menu_width={menu_width}>
        <h1>게시판</h1>
       
      </ContentsBoxBlock>
   
          
     
    </>
  );
};

export default ContentsBox;
