import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from "react-router-dom";
import {WhitePostsItemBox} from '../common/WhiteBox';
const ReplyListBlock = styled.div`
  
`;

const ReplyItemBlock = styled(WhitePostsItemBox)`
`
const ReplyItemInfoBlock = styled.div`

  padding-top: 1rem;
  padding-bottom: 1rem;

  &:first-child {
    padding-top: 0;
  }
  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h2 {
    font-size: 2rem;
    margin-bottom: 0;
    margin-top: 0;
    &:hover {
      color: ${palette.gray[6]};
    }
  }

  p {
    margin-top: 2rem;
  }
`;

const Contents =styled.div`
  margin-Top: 10px;
`


const PostContent = styled.div`
font-size: 1rem;
padding-top:0;
`;

const ReplyItem = ({ reply }) => {

    return (
    
    <ReplyItemBlock>
        <ReplyItemInfoBlock>
        {reply.reply_name}
        </ReplyItemInfoBlock>
   
    </ReplyItemBlock>
    );
};
  const ReplyList = ({ replys, loading, error, showWriteButton }) => {
    
    const replysArray = [
        {
            reply_id: 0,
            reply_name: '유저1',
            reply_contents: '테스트 내용이에요',
        },
        {
            reply_id: 1,
            reply_name: '유저2',
            reply_contents: '테스트 내용이에요',
        }
    ]
    if (error) {
      return <>게시판을 불러올 수 없습니다...</>
    }
  /**에러처리 */
    return (
      <ReplyListBlock>
        replysArray.map(reply => (
            <ReplyItem reply={reply} key={reply.reply_id} />
        {!loading && replys && (<div>
          {
            replys.map(reply => (
            <ReplyItem reply={reply} key={reply.reply_id} />
          ))}
        </div>)}
      </ReplyListBlock>
    );
  };
  
  export default ReplyList;
