import styled from 'styled-components';
import palette from '../../lib/styles/palette';

const SearchBlock = styled.div`
display: flex;
flex-direction: row;
height: 3rem;
width:30rem;
border: thin solid;
`;
const SearchInput = styled.input`
width:10rem;
height:1.7rem;
margin-right:0.5rem;
margin-left:5rem;
`
const SearchIcon = styled.img`
width:2rem;
height:2rem;
border: thin solid;
padding: 5px;
background: ${palette.green[0]};
border-radius: 10rem;
&:hover{
    background: ${palette.gray[3]};
}
&:active{
    background: ${palette.gray[5]};
}
`

const SearchBtn = ()=>{

    return <>
 
        <SearchIcon src={process.env.PUBLIC_URL +"/search-icon.png"}/>

    </>
}

const Search = () => {
  return <SearchBlock >
    <SearchInput/>
    <SearchBtn/>
  </SearchBlock>;
};

export default Search;
