import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';
import Responsive from '../common/ResponsiveHeader';
const PostListBlock = styled(Responsive)`
  margin-top: 3rem;
  
`;

const MypageTemplateBlock = styled.div`
display: flex;
 width: 50rem;
 height: 30rem;
 display: flex;
margin-bottom:1rem;
border: thin solid ;
border-color: #424242;
box-shadow: 5px 5px 5px rgba(10, 10, 10, 0.3);
`;
const MypageInput =styled.input`
height: 2.5rem;
font-size:2rem;
`

const MyInfoBlock =()=>{

  return(
    <MypageTemplateBlock>
      <h1>개인 정보</h1>
      <MypageInput type='text'/>
    <></>
  </MypageTemplateBlock>
  )
};

const MypageTemplate = ({ children }) => {
  return (
    <PostListBlock>
    <MyInfoBlock/>
      
    </PostListBlock>
  );
};

export default MypageTemplate;
