import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Button from '../common/Button';
import Responsive from '../common/Responsive';
import WhitePostsItemBox from '../common/WhiteBox';

const ReplyEditorBlock = styled.div`
flex-wrap: nowrap;

`;


const ReplyEditorBackground = styled.div`
display:flex;
flex-direction:column;
margin-bottom:0.5rem;
border: thin solid ;
border-color: #424242;
box-shadow: 5px 5px 5px rgba(10, 10, 10, 0.1);
padding:1rem 1rem 0.5rem 0.5rem;
flex-wrap: nowrap;
font-size: 0.9rem;
background: ${palette.gray[0]};
`
const InputReplyDiv = styled.div`
display: flex;
flex-direction: row;
margin-left:0.5rem;
margin-right:0.5rem;
margin-bottom:0.5rem;
 text-align: center;

`
const NickName = styled.div`

`
const InputReplyContents = styled.input`
text-align: left;
padding: 5px;
margin: 0px;
width: 48rem;
height: 6rem;
margin-left: auto;

`
const LocationLoginBtn = styled(Button)`
 
  width:18rem;
  height:2.8rem;
  font-size: 1.2rem;
  text-align: center;
  margin-left:auto;
  margin-right:auto;
`
const SubmitBtn = styled(Button)`
margin-left: auto;
width:5rem;
height:2rem;
margin-right:0.5rem;
margin-bottom:0rem;
`
const Text = styled.h3`

  margin: 0;
  margin-left: auto;
  margin-right: auto;

`

const ReplyEditorItem = ({user, onChangeReplys, onPublish})=>{


    return (<ReplyEditorBackground>
            <InputReplyDiv>
            <NickName><b>{user.user_name}</b></NickName>
            <InputReplyContents onChange={onChangeReplys}/>
            </InputReplyDiv>
            <SubmitBtn green onClick={onPublish}>등록</SubmitBtn>
        </ReplyEditorBackground>);

}

const ReplyEditor = ({user,onLocationLogin, onChangeField, onPublish, onCancel }) => {
  const userEx = {
    user_name:'닉네임'
}
  const onChangeReplys= e=>{
    onChangeField({ key: 'reply_contents', value: e.target.value });
  }
  return (
user?
    <ReplyEditorBlock>
    <ReplyEditorItem user={user?user:userEx} onChangeReplys={onChangeReplys}
    onPublish={onPublish}
    />
    </ReplyEditorBlock>:(
    <ReplyEditorBlock>
    <ReplyEditorBackground>
    <Text>댓글은 로그인 후 작성 할 수 있습니다.</Text>
    <InputReplyDiv>
   <LocationLoginBtn onClick={onLocationLogin} green>로그인 하러가기</LocationLoginBtn>
   </InputReplyDiv>
   </ReplyEditorBackground>
    </ReplyEditorBlock>)
    

  );
};

export default ReplyEditor;
