import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import WhitePostsItemBox from '../common/WhiteBox';

const ReplyEditorBlock = styled.div`
flex-wrap: nowrap;
`;


const ReplyEditorBackground = styled.div`
margin-bottom:0.5rem;
border: thin solid ;
border-color: #424242;
box-shadow: 5px 5px 5px rgba(10, 10, 10, 0.1);
padding:1rem 1.2rem 0rem 1.2rem;
flex-wrap: nowrap;
font-size: 0.9rem;
flex-wrap: nowrap;
`

const ReplyEditor = ({}) => {
  const user = {
    user_name:'닉네임'
}
  return (
    <ReplyEditorBlock>
    <ReplyEditorItem user={user}/>
    </ReplyEditorBlock>
  );
};

export default ReplyEditor;
